package skyestudios.buildx.activities;import android.app.ProgressDialog;import android.content.Intent;import android.os.Bundle;import android.widget.TextView;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;import androidx.recyclerview.widget.LinearLayoutManager;import com.google.gson.Gson;import java.io.File;import java.util.ArrayList;import java.util.List;import skyestudios.buildx.adapters.ProjectAdapter;import skyestudios.buildx.base.BaseActivity;import skyestudios.buildx.base.BaseDialogFragment;import skyestudios.buildx.databinding.ActivityHomeBinding;import skyestudios.buildx.dialogs.DialogNewProject;import skyestudios.buildx.helpers.FileUtil;import skyestudios.buildx.helpers.IOUtils;import skyestudios.buildx.helpers.Methods;import skyestudios.buildx.helpers.Utils;import skyestudios.buildx.models.Project;import skyestudios.buildx.models.Projects;public class HomeActivity extends BaseActivity implements ProjectAdapter.OnProjectClicked {	private ActivityHomeBinding binding;	private ProjectAdapter adapter;	@Override	protected void onCreate(@Nullable Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		binding = ActivityHomeBinding.inflate(getLayoutInflater());		setContentView(binding.getRoot());		iniLoading();		init();	}	private void init(){		binding.createNewProject.setOnClickListener(view -> {			Methods.getInstance().createNewProject(this);		});		adapter = new ProjectAdapter(this,getProjects(),this);		binding.projectList.setLayoutManager(new LinearLayoutManager(this));		binding.projectList.setHasFixedSize(true);		binding.projectList.setAdapter(adapter);	}	private List<Project> getProjects(){		List<Project> projectList = new ArrayList<>();		String projectJson = FileUtil.readFile(Utils.getProjectList().getAbsolutePath());		if (!projectJson.isEmpty()) {			Projects projects = new Gson().fromJson(projectJson,Projects.class);			projectList = projects.projectList;		}		return projectList;	}	@Override	public void onBackPressed() {		super.onBackPressed();	}	@Override	public void projectItemClicked(Project project) {		showLoading();		Intent intent = new Intent(this,MainActivity.class);		intent.putExtra(MainActivity.PROJECT,project);		startActivity(intent);	}	@Override	protected void onActivityResult(int requestCode, int resultCode, @Nullable Intent data) {		super.onActivityResult(requestCode, resultCode, data);		List<Fragment> listOfFragments = getSupportFragmentManager().getFragments();		for (Fragment navfragment: listOfFragments){			if (navfragment instanceof DialogNewProject){				navfragment.onActivityResult(requestCode,resultCode,data);			}		}	}}